const twoDecimals = (number) => parseFloat(number).toFixed(2)
const ShopProduct = ({ title, imageUrl, price, description, item, addItem, linkUrl, history, match }) => {
    return (
        <Container className='product-item'>
            <Row>
                <Col xs={12} md={6}>
                    <Image
                        fluid
                        src={imageUrl} alt="" />
