                <Col xs={6} md={4}>
                    <h3>{title}</h3>
                    <p>${twoDecimals(price)}</p>
                    <p>{description}</p>
                    <button onClick={() => addItem(item)}>Add to cart</button>
                </Col>
            </Row>
        </Container>
    );
};


